frappe.ui.form.on('Material Request', {
	refresh: function(frm) {

        // Check if created from Production Plan
        if (frm.doc.production_plan) {

            // Set only Purchase as option
            frm.set_df_property('material_request_type', 'options', ['Purchase']);

            // Force value to Purchase
            frm.set_value('material_request_type', 'Purchase');

            // Make field read-only
            frm.set_df_property('material_request_type', 'read_only', 1);
        }
    }
})

frappe.ui.form.on('Production Plan', {
    refresh: function(frm) {

        // Filter Item in Assembly Items
        frm.set_query('item_code', 'po_items', function(doc, cdt, cdn) {
            return {
                filters: {
                    item_code: ['like', 'FG%']
                }
            };
        });

        // Filter Warehouse in Assembly Items
        frm.set_query('warehouse', 'po_items', function(doc, cdt, cdn) {
            return {
                filters: {
                    name: ['like', 'FG%']
                }
            };
        });

        // Filter Sub Assembly Warehouse (Parent Field)
        frm.set_query('sub_assembly_warehouse', function() {
            return {
                filters: {                     
					name: ['like', 'FG QUARANTINE%']
                }
            };
        });

    }
});
